import React,{useEffect,useState} from 'react'
import axios from "axios";

function App() {

    const [weather, setWeather] = useState(null)
    const [input, setInput] = useState('')

    useEffect(() =>{
        axios.get('http://api.weatherapi.com/v1/current.json?key=c48f289c21554487bee92730211805&q=Urgench&aqi=no')
            .then(data => setWeather(data))
    }, [])

    function inputHandler(event){
        setInput(event.target.value)
    }

  return (
    <div className="App">
        <input type="text" onChange={inputHandler}/>
        {weather &&(
            <div>
                <h1>{weather.data.location.name}</h1>
                <img src={weather.data.current.condition.icon}/>
                <h2>{weather.data.current.condition.text}</h2>
                <h1>{weather.data.current.temp_c} C</h1>
            </div>
        )}
    </div>
  );
}

export default App;
